// Works best with perlin noise.

vec4 fire(vec2 uv, float xMul, float time )
{    
    float realTime = iTime*time;

    float topBottomStrength = 2.5; // 2.5
    float leftRightStrength = 12.; // 12.
    float verticalMiddleStrength = 0.8; // 0.8

    float topBottomGradient = 1.0 - min((1.0 - uv.y) * 1.0, 1.0) * topBottomStrength; 
    float leftRightGradient = verticalMiddleStrength + pow(abs(uv.x - 0.5) * 0.5, 1.5) * leftRightStrength;

    float fireStrength = 0.55; // 0.55 
    float fireDepth = 700.; // 700.
    float fireExposure = 1.7; // 1.7

    vec2 noiseStretch = vec2(0.6 * xMul, 0.3);
    vec2 noiseCoord = vec2(uv * noiseStretch) + vec2(0.0, realTime);
    float noise = texture(iChannel0, noiseCoord).x;

    float flames = pow(noise * fireStrength,  leftRightGradient) * pow(noise * fireStrength, topBottomGradient);

    float f = pow(1.0-(flames*flames*flames), fireDepth);
    float fff = f*f*f;
    vec3 fire = vec3(f, fff, fff*fff*fff) * fireExposure;
    return vec4(vec3(fire), f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.y = 1.0 - uv.y;

    vec4 fire1 = fire(uv, 1.0, 0.12);
    vec4 fire2 = fire(uv, -1.0, 0.13);

    fragColor = max(fire1, fire2);

}
