#define t iTime+5000.

float hash(float z) {
    z = fract(sin(dot(z*.329587239857,t)));
    z = abs(z);
    return z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);
    uv*=10.;

    vec3 col = vec3(0);
    col += hash(sin(hash(-hash(1./(hash(uv.y)+uv.x)))));

    fragColor = vec4(col,1.0);
}
