#define R3D(a) mix(q*dot(p,q),p,cos(a))+sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,t=iTime; vec3 p,q=vec3(1,-1,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*3.;
    p.z-=1.;
    p=R3D(t*.5);
    p=mod(p,.2);

    for(j=0.;j++<8.;)
        p=(abs(p/d-.02)-.02)-.01,
        d=max(distance(p.zy,p.xz)/d,p.z-.05),
        d-=dot(p,p)/1e4;

    e-=d;

}o+=vec4(exp(p*p*q),1)*2./i;
}
