void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy / 2.0) / iResolution.y;

    float amp = 0.02;
    float freq = 10.0;

    float theta = atan(uv.y, uv.x);
    uv.x += sin(theta * freq + iTime) * amp;
    uv.y += cos(theta * freq + iTime) * amp;

    float torus = 0.005 / abs(length(uv) - 0.3);
    fragColor = vec4(torus);
}
