float fmod(float a, float b){
    float
    t1 =
        mod(floor(b/2.),2.)    ,
    a1 =
        (a+t1),
    a2 = mod(a1,3.),
    a3 =
        mod(a1-mod(a1,2.),4.)
        //mod(a1-mod(a1,2.),4.)+floor(a1/8.)
    ;
    return
        mod(a1-a2-a3,b)
    ;
}


