      8.,
  tempo1 =
      //1.
      //divide by prime numbers to get an interesting rhythm
      prime_rhythm(time)
      //prime_rhythm(time+collatz(time,time))
  ,
  t = s1*time/4./tempo1,
  t4 = t/prime_rhythm(time+11.)/(1.+fmod(t/8.,2.)),
  t1 =
      fmod(t4,2.)
      //fmod(t4+collatz(t4,16.),2.)
  ,
  t5 = t*2./prime_rhythm(time+7.),
  t3 = (1.+fmod(t5,2.)),
  t6 = time*8./t3,
  m3 =
      floor(fmod(t6+mod(t6/8.,3.)-mod(t6/8.,2.),s1/2.)/2.),
      //floor(fmod(t6+floor(t6/8.),s1/2.)/2.),
      //floor(2.*fmod(floor(t6)*3.,s1/2.)/2.),
  m2 =
      1. + fmod1(t/t3/2.,s1/2.);

  duration *= tempo1;

  vec2
  a= vec2(
      //-log(fract(t/prime_rhythm(time+7.))/4.)*4.
      //-log(fract(t/2./prime_rhythm(time+14.))*fract(t/prime_rhythm(time+7.))/8.)*4.

      //sitar
      //-(log(1./64./4.+fract1(t/2./prime_rhythm(time+14.))/16.)),-(log(1./64./4.+fract1(t/prime_rhythm(time+7.))/16.))

      -vec2(log(1./64.+fract1(t/2./prime_rhythm(time+14.))/8./duration),log(1./64.+fract1(t/prime_rhythm(time+7.))/8./duration))

      //steel drum / gamelan
