  + log(1./8.+a*abs(1./4.-abs(.5-vec2(fract1(nb.x*2.*.998),fract1(nb.y*2.)))/4.*m2)))
  ,harp =
      a/16./4.*abs(
      synth(1./4.,a,1./8.,1.,nb*4.,2.)
      + synth(1./8.,a,1./4.,1.,nb*2.,1./2.))
  ;
  switch(instrument){
