      //abs(log(abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a/2.))/2.
      //abs(1.+log(abs(.5-vec2(fract(nb.x*.998),fract(nb.y)))*a/2.))/2.

      //log(a*abs(1./4.-abs(.5-vec2(fract1(nb.x+.5),fract1(nb.y*2.)))))
  ;
  case 2: return sitar;
  case 3: return oud;
  case 4: return steel_drum;
  case 5: return banjo;
  case 6: return guitar;
  case 7: return drum;
  case 8: return synth_drum;
  case 9: return synth_drum1;
  default: return sitar;
  }
}

