#define h21(a) fract(1e3 * sin(dot(a, r)))
#define pal(a) (.8 + h21(a+.1) / 2.6 * cos(2.* pi * h21(a) + vec4(0, 23, 21, 0)))
#define pi acos(-1.)

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy;
         u = (u + u - r) / r.y;

    u = vec2(
            log(dot(u, u)),
            atan(u.x, u.y) * 6. / pi // tks @fabrice!
        ) + iTime * .3;

    u += sin(
             tanh(
                 cos(iTime) * 3. - 2.
             ) * 5. + u.yx * pi
         ) * .1;

    o.zw = mod(floor(u), 12.); // tks @fabrice!
    o.x = .52 - length(fract(u) - .5);
    o.x = iMouse.z > 0.? abs(o.x) : max(o.x, 0.);
    o = pow(o.x + pow(o.x + .43, 30.) * 5., .3) * pal(o.zw);
}
