const float PI = 3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);
    vec2 m_uv = iMouse.xy/min(iResolution.x, iResolution.y);

    float scale = 10.;

    vec2 scaled_uv = uv * scale;
    vec2 uv_remainder = mod(scaled_uv, 1.);
    vec2 s_uv = scaled_uv - uv_remainder; // stepped uv

    vec2 scaled_m_uv = m_uv * scale;
    vec2 m_uv_remainder = mod(scaled_m_uv, 1.);
    vec2 sm_uv = scaled_m_uv  - m_uv_remainder;
    float mouse_dist = distance(scaled_uv, scaled_m_uv);
    float s_mouse_dist = distance(s_uv+vec2(.5), scaled_m_uv);


    float mouse_mixin = 1.-pow(clamp(mouse_dist/3., 0., 1.), 2.);
    float s_mouse_mixin = 1.-pow(clamp(s_mouse_dist/3., 0., 1.), 2.);


    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    col = vec3(0.141,0.341,0.941);
    col = mix(col, vec3(0.976,0.310,0.263), s_mouse_mixin);

    vec2 smooth_dist_vec = pow((uv_remainder - vec2(.5))*2., vec2(4.));

    float smooth_dist = pow(smooth_dist_vec.x + smooth_dist_vec.y, 1./4.);

    float val = sin(iTime + s_uv.x/2.)/2. + .5;
    val = mix(val, 1., mouse_mixin);

    float lower = 1.-cos(val * PI/2.);
    float upper = sin(val * PI/2.);

    lower = pow(val, 2.);
    upper = 1.-pow(val - 1., 2.);

    val = smoothstep(lower*0.9, .001+upper*0.999, smooth_dist);

    col = (1.-val) * col;

    fragColor = vec4(col,1.0);
}
