//DAYTONA
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 n = fragCoord / iResolution.xy;
vec2 m = fragCoord / iResolution.xy;
float ncos = cos(iTime) * 0.5 + 0.5;
float msin = sin(iTime + 4.7) * 0.5 + 0.5;

fragColor = vec4(msin * n.x + ncos * m.y, 0.0, 0.0, 1.0);
}
