// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float dist1 = abs(mod((uv.x+uv.y)*2.,0.6)-0.1)/0.2;
    float dist2 = abs(mod(-(uv.x+uv.y)*2.,0.6)+0.1)/0.2;
    float d = min(dist1,dist2);
    // Time varying pixel color
    float r = mix(1.,0.,(d*d));


    float t = iTime/20.;
    dist1 = abs(mod((uv.x+uv.y+t)*2.,0.6)-0.1)/0.2;
    dist2 = abs(mod(-(uv.x+uv.y+t)*2.,0.6)+0.1)/0.2;
    d = min(dist1,dist2);

    float g = mix(1.,0.,(d*d));

    dist1 = abs(mod((uv.x+uv.y-t)*2.,0.6)-0.1)/0.2;
    dist2 = abs(mod(-(uv.x+uv.y-t)*2.,0.6)+0.1)/0.2;
    d = min(dist1,dist2);

    float b = mix(1.,0.,(d*d));
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
