float hash(float n) { 
    return fract(sin(n) * 43758.5453); 
}


float noise(vec2 p) {
    return hash(p.x * 37.0 + p.y * 57.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Hint, it's a few lines of code, and makes use of a tan function :)
    vec2 warpedUv = uv;

    float low = mod(iTime * 0.2, 1.0);
    float hiph = low + 0.10f;

    if (warpedUv.y > low && warpedUv.y < hiph) {
        warpedUv.x += 0.01f * tan(200.f * uv.y + 50.f * iTime);
    }

    float timeFactor = iTime * 0.1;

    float random1 = noise(uv);
    float random2 = noise(uv + 5.4);
    float random3 = noise(uv + 6.8);

    vec2 offset1 = vec2(0.03 * random1, 0.03 * sin(iTime ));
    vec2 offset2 = vec2(-0.002 * sin(iTime * 1.0), 0.002 * sin(iTime * 1.0));
    vec2 offset3 = vec2(-0.03 * random3, -0.03 * sin(iTime * 1.2 ));

    float r = texture(iChannel0, warpedUv + offset1).r;
    float g = texture(iChannel0, warpedUv + offset2).g;
    float b = texture(iChannel0, warpedUv + offset3).b;

    vec3 col = vec3(r, g, b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
