void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // M.I.T. License.
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 tiledUV = uv * 10.0;
    float aspect = iResolution.x/iResolution.y;
    tiledUV.x *= aspect;
    float checkerValue = mod(floor(tiledUV.x+2.0*fract(iTime)) + floor(tiledUV.y+2.0*sin(iTime)), 2.0);
    vec3 calculatedColor = checkerValue * vec3(1.0); // 0.0 for black, 1.0 for white

    // Output to screen
    fragColor = vec4(calculatedColor,1.0);
}
