// Forked from https://www.shadertoy.com/view/MsBfzm

float random (in float x) {
    return fract(sin(x)*1e4);
}

float random (in vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))* 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouseCoord = iMouse.xy/iResolution.xy;
    float rows = 5.0;
	vec2 uv = fragCoord/iResolution.xy;

    float curThickness = (sin(iTime * 1.0)* 0.3 + 0.25);
    //curThickness = 0.25;


    float yRepeat = fract(uv.y * rows + random(uv.y)*0.2);		

    float aa =  0.03; 	
    float f = length(yRepeat - 0.5);
    //float col = step(f, curThickness);
    float col = smoothstep(curThickness - aa, curThickness + aa, f);

	fragColor = vec4(vec3(col),1.0);
}
