#define PI 3.1415926
void mainImage(out vec4 fragColor, in vec2 fragCoord)  
{  
    vec3 col = vec3(1.0);  
    vec2 uv = fragCoord / iResolution.xy - 0.5;  
    uv.x *= iResolution.x / iResolution.y;  

    // 圆形和椭圆  
    float circle = smoothstep(0.1, 0.1, pow(uv.x, 2.0) + pow(uv.y - 0.07, 2.0));  
    float ellipse = smoothstep(0.0045, 0.005, pow(uv.x / 4.0, 2.0) + pow(uv.y + 0.4, 2.0));  

    // 正弦波  
    bool isInsideSineL1 = uv.y >= sin(5.0 * (uv.x + iTime*0.8) + 0.5) / 8.0 + 0.1975 && uv.y <= sin(5.0 * (uv.x + iTime*0.8) + 0.5) / 8.0 + 0.2025;  
    bool isInsideSineL2 = uv.y >= sin(3.0 * (uv.x + iTime*1.1) + 0.5) / 5.0 + 0.0975 && uv.y <= sin(3.0 * (uv.x + iTime*1.1) + 0.5) / 5.0 + 0.1025;  
    bool isInsideSineR1 = uv.y >= sin(5.0 * (uv.x + iTime*0.9) + 0.5) / 8.0 - 0.2025 && uv.y <= sin(5.0 * (uv.x + iTime*0.9) + 0.5) / 8.0 - 0.1975;  
    bool isInsideSineR2 = uv.y >= sin(3.0 * (uv.x + iTime*1.2) + 0.5) / 5.0 - 0.1025 && uv.y <= sin(3.0 * (uv.x + iTime*1.2) + 0.5) / 5.0 - 0.0975;

    // 调制颜色（考虑圆形和椭圆）  
    vec3 modulatedColor = col * circle * ellipse;  

    // 设置 fragColor  
    if (isInsideSineL1 || isInsideSineL2)  
    {  
        fragColor = vec4(0.0, 0.0, 0.0, 1.0); // 正弦波L1,2为黑色  
    }  
    else if (isInsideSineR1 || isInsideSineR2)  
    {  
        fragColor = vec4(1.0, 1.0, 1.0, 1.0); // 正弦波R1,2为白色  
    }  
    else 
    {  

        fragColor = vec4(modulatedColor, 1.0); // 其他区域为圆形和椭圆的调制颜色
        if (uv.y - 0.07 < uv.x * sin(iTime*2.0 + PI)*100. && pow(uv.x, 2.0) + pow(uv.y - 0.07, 2.0)<0.1)    //旋转处理
        {  
            fragColor = vec4(1.0 - fragColor.rgb, fragColor.a);  
        }  

        else if(pow(uv.x, 2.0) + pow(uv.y - 0.07, 2.0)>=0.1)
        {
            if (uv.x > 0. && tan(iTime*1.) < 0.)
            {
                fragColor = vec4(1.0 - fragColor.rgb, fragColor.a);
            }
            if (uv.x < 0. && tan(iTime*1.) > 0.)
            {
                fragColor = vec4(1.0 - fragColor.rgb, fragColor.a);
            }
        }
    }
        // Time varying pixel color
    vec3 col_ = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));


    fragColor = vec4(0.25 + fragColor.rgb +col_, fragColor.a);//全局调色
}
