void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float width = 96.;
    float height = 54.;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 puv = vec2(floor(uv.x*width)/width,floor(uv.y*height)/height);


    // Time varying pixel color
    vec3 col = texture(iChannel0,puv).xyz;
    vec3 gray = vec3(length(col))/3.;
    float diffsum = 0.;
    for(float i=-2.;i<2.1;i = i + 1.){
        for(float j=-2.;j<2.1;j = j + 1.){
            vec3 samp = texture(iChannel0,puv + vec2(i/width,j/height)).xyz;
            diffsum = diffsum + dot(samp-gray,samp-gray) * (i == 0. && j ==0. ? -24. : 1.);
        }
    }

    if(diffsum <= 1. && length(col-vec3(0.,.64,0.)) <= 0.4)
        fragColor = vec4(texture(iChannel1,uv).xyz,1.0);
    else
        fragColor = vec4((1.-vec3(diffsum))*vec3(0.8,0.4,0.3),1.0);
}
