void mainImage( out vec4 fragColor, vec2 fragCoord ) {
    vec2 uv = fragCoord.xy / iResolution.y;
    float aspectRatio = iResolution.x/iResolution.y;


    float startX = 0.5;
    float startY = 0.5;
    float blurRadius = 0.3;
    float rotationRadius = 0.3;

    float displacementX = sin(2.0 * 3.14 * iTime) * rotationRadius;
    float displacementY = cos(2.0 * 3.14 * iTime) * rotationRadius;


    fragColor = vec4(1. - length( uv.xy - vec2(startX * aspectRatio + displacementX, startY + displacementY)) / blurRadius);

}
