// 2024年9月18日 13时32分
// optical illusion 视觉误差


// 204 chars thanks to SnoopethDuckDuck
void mainImage(out vec4 o, vec2 u) 
{
    vec2 k = 2. * round(u *= 9. / iResolution.y);           

    vec4 c; 
    c.x = .2;   
    o = .44 - c;

    if ((int(u) + int(u.y)) % 2 < 1)
        o.y = .65;

    if (max(length(u = abs(u+u-k)) - .3, min(u.x, u.y)) < .08)
        o = sin(k.x + k.y) < 0. ? .24 - c : o/o;   
}




