void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //uv.x *= iResolution.x / iResolution.y;

    vec2 grid = (fract((uv) * 5.)- .5);

    float d = length(grid);



    vec4 tex = texture(iChannel0, uv);


    vec4 color = tex;
    color *= step(d, abs(sin(iTime)));

    fragColor = color;
}
