#define rotation(angle) mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
float TAU = 2.*3.14159;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = ( fragCoord - .5* iResolution.xy ) /iResolution.y;
   vec3 col = vec3(0.);
   float t = fract(.1 * iTime);
   uv *= rotation(TAU/ 12. + TAU * pow(1. - length(uv),9.));  
   for (float i = 0.; i < 5.; i++){
     float y = 2.5 * sin(TAU *(-t + uv.x + i / 5.));
     float d = 1. - length(uv);
     col += step(abs(uv.y - d * d * d * d * y) - .04,0.);
   }  
   fragColor = vec4(col,1.0);
}
