float SDcircle(vec2 p ,vec2 uv,float r){
    return length(p-uv) - r;
}
float Circle(vec2 p,vec2 uv,float r,float blur){
    float d = length(p - uv);
    d = smoothstep(r,r-blur,d);
    return d;
}
vec2 pos(float off){
    vec2 pos = vec2(0.5,0.5);
    pos *= vec2(sin((iTime-off)*3.)+cos((iTime-off)*4.),cos((iTime-off)*3.)+sin((iTime-off)*1.));
    return pos;
}
float r (float off){
    float r;
    r = 0.25 + 0.18*sin((iTime-off)*2.);
    return r;
}
float blur(float off){
    float blur;
    blur = 0.03+0.03*(sin((iTime-off)*2.));
    return blur;
}
