// https://iquilezles.org/articles/distfunctions2d/

float sdEgg( in vec2 p, in float ra, in float rb )
{
    const float k = sqrt(3.0);

    p.x = abs(p.x);

    float r = ra - rb;

    return ((p.y<0.0)       ? length(vec2(p.x,  p.y    )) - r :
            (k*(p.x+r)<p.y) ? length(vec2(p.x,  p.y-k*r)) :
                              length(vec2(p.x+r,p.y    )) - 2.0*r) - rb;
}

vec3 palette( float t ) 
{
    vec3 a = vec3(0.0, 0.5, 0.5);
    vec3 b = vec3(0.0, 0.5, 0.5);
    vec3 c = vec3(0.0, 0.333, 0.0);
    vec3 d = vec3(0.0, 0.5, 0.0);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;


    vec2 uv1 = uv; //*= 1.125;
    vec2 uv2 = uv; //*= 1.5;
    vec2 uv3 = uv; //*= 2.0;
    vec2 uv4 = uv; //*= 2.0;
    //uv -= 1.0;

    float a = 0.0;
    float b = 0.0;
    float c = 0.0;
    float d = 0.0;

    if (uv1.x <= -0.5) a = 1.0;
    else if (uv2.x <= 0.0) b = 1.0;
    else if (uv3.x <= 0.5) c = 1.0;
    else if (uv4.x <= 1.0) d = 1.0;


    uv.y += (iTime*2.5);
    uv.x += cos(iTime* 0.5 + 0.5);

    uv1.y += (iTime * 3.) + cos(iTime - 1.37 * 1.125 + 1.125);
    uv1.x += cos(.625 + iTime* 1.125 * 0.5 + 0.5);

    uv2.y += (iTime * 3.5) + cos(iTime * 1.7 + 1.7);
    uv2.x += cos(.125 + iTime* 1.5 * 0.5 + 0.5);


    float t = cos(iTime) * 0.2 + 0.2;

    float t1 = cos(iTime) * 0.005 + 0.005;

    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 2.0; i++)
    {
        vec3 color = palette(length(uv) + iTime / 2.25);        
        uv = fract(uv * 1.) - 0.5;              
        d = sdEgg(uv, t1 - .02, t1 - 0.25 );               
        d = pow(0.025 / clamp(d, 0.01, 1.0), 1.);

       //------------------------------------------------

        vec3 color1 = palette(length(uv1) + iTime / 1.5); 
        uv1 = fract(uv1 * 1.) - 0.575;              
        float d1 = sdEgg(uv1, t1 - .015, t1 - 0.35 );
        d1 = pow(0.025 / clamp(d1, 0.01, 1.0), 2.);

       //------------------------------------------------

        vec3 color2 = palette(length(uv2) + iTime); 
        uv2 = fract(uv2 * 1.) - 0.525;              
        float d2 = sdEgg(uv2, t1 - .01, t1 - 0.3 );
        d2 = pow(0.025 / clamp(d2, 0.01, 1.0), 3.);


        finalColor += color * d + color1 * d1 + color2 * d2;
    }

    fragColor = vec4(finalColor ,1.0);

}
