//parameters

#define n1 0.4 //variation amplitude
#define n2 0. //non-linearity
#define n3 0.1 //linear addition
#define n4 1. //speed
#define n5 1. //zoom
#define n6 0.5 //baseline amplitude
vec3 amp =  vec3 (0.07, 0.07, 0.07); //color-specific amplitude
vec3 mred = vec3 (0.31, 0.1, 0.4); //red modulation
vec3 mgreen = vec3 (0.17, 0.25, 0.2); //green modulation
vec3 mblue = vec3 (0.43, 0.47, 0.40); //blue modulation
vec3 fred = vec3 (0.23, 0.14, 0.2); //red frequency
vec3 fgreen = vec3 (0.11, 0.18, 0.15); //green frequency
vec3 fblue = vec3 (0.17, 0.1, 0.2); //blue frequency

float zdef(float x, float y) { //definition of z-parameter
        return 1.-(x*x+y*y)/2.;
    }

// main functions
    float f(float x, float y, float z) {
        return n2*y*z+max(max(mod(x,n6*mred.x+n1*sin(n4*fred.x*iTime)), mod(y,n6*mred.y+n1*sin(n4*fred.y*iTime))),mod(z,n6*mred.z+n1*sin(n4*fred.z*iTime)))+amp.x+n3;
    }

    float g(float x, float y, float z) {
        return n2*x*z+max(max(mod(x,n6*mgreen.x+n1*sin(n4*fgreen.x*iTime)), mod(y,n6*mgreen.y+n1*sin(n4*fgreen.y*iTime))),mod(z,n6*mgreen.z+n1*sin(n4*fgreen.z*iTime)))+amp.y+n3;
    }
    float h(float x, float y, float z) {
        return n2*x*y+max(max(mod(x,n6*mblue.x+n1*sin(n4*fblue.x*iTime)), mod(y,n6*mblue.y+n1*sin(n4*fblue.y*iTime))),mod(z,n6*mblue.z+n1*sin(n4*fblue.z*iTime)))+amp.z+n3;
    }

//core
void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = n5*fragCoord / iResolution.xy-n5/2.;

    // Iterations
    int n = 50;

    // Initial values
    float x = uv.x, y = uv.y, z=zdef(uv.x, uv.y);

    for(int i = 0; i < n; ++i) {
        float newX = f(x, y, z);
        float newY = g(x, y, z);
        float newZ = h(x ,y ,z);
        x = newX;
        y = newY;
        z = newZ;
    }

    vec3 col = vec3(abs(x),abs(y),abs(z));

    fragColor = vec4(col, 1.0);
}
