void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame%2<1) discard;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(0.0);

    col = vec3(texture(iChannel0,uv).r);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define rad .6*iResolution.y
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uvinc = 1.0/iResolution.xy;
    vec2 uv = fragCoord*uvinc;
    float s = (texture(iChannel0,uv).r)*0.2;
    float a = texture(iChannel0,uv+vec2(uvinc.x,0.0)).r;
    a += texture(iChannel0,uv+vec2(0.0,uvinc.y)).r;
    a += texture(iChannel0,uv-vec2(uvinc.x,0.0)).r;
    a += texture(iChannel0,uv-vec2(0.0,uvinc.y)).r;
    if (iMouse.z > 0.0&&iFrame%2==1) fragColor = vec4(2.5/vec3((rad-distance(fragCoord,iMouse.xy))),1.0);
    else
    {
        fragColor = vec4(vec3(10.0/a),1.0);
    }
    if (iFrame<4) fragColor = vec4(2.5/vec3((rad-distance(fragCoord,.5*iResolution.xy))),1.0);
}
