#define video(uv) texture(iChannel1,uv)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = abs(video(uv)-(backbuffer(uv)));//*.9999;
    fragColor = mod(fragColor,.7);//+0.0001;
