void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv =  fragCoord / iResolution.xy;
    vec2 pixelSize = 1.0 / iResolution.xy;

    float str = fract(uv.x + iTime * 0.25);
    vec2 offset = pixelSize * vec2(0.0, 1.0);

    vec4 colorAbove = texture(iChannel0, uv - offset);
    vec4 colorHere  = texture(iChannel0, uv);
    vec4 colorBelow = texture(iChannel0, uv + offset);

    vec4 finalColor = colorHere;

    float blu = texture(iChannel2, fragCoord / 1024.0 + iTime).r;
    if (fract(blu) >= 0.5) {
        if (length(colorAbove) < length(colorHere)) {

            finalColor = colorAbove;

        }
    } else {
        if (length(colorHere) < length(colorBelow)) {

            finalColor = colorBelow;

        }
    }

    finalColor = mix(
        finalColor,
        texture(iChannel1, uv),
        smoothstep(
            0.05,
            0.0,
            str
        )
    );


    fragColor = finalColor;
}
