#define DOTS 100
#define PHI 0.618033989
#define TAU 6.28318531
#define EPSILON 0.00000000001
#define PCOL vec3(1., 0.5, 0.25)
#define NCOL vec3(0.25, 0.5, 1.)
#define ZCOL vec3(0.)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy/2.) / length(iResolution.xy) * 2.;
    float arad = sqrt(1./float(DOTS+1));

    float dd = pow(smoothstep(arad*0.5,arad*1.5,length(uv)),.25);
    vec2 md;
    float j,theta;

    for(int i=0;i<DOTS;i++){
        j = float(i) - fract(iTime * TAU / 300.);
        theta = j*PHI*TAU + length(uv);
        md = vec2(sin(theta),cos(theta))*sqrt(j/float(DOTS));
        dd *= pow(min(1.,length(uv-md)/arad),.25);

    }

    for(int i=0;i<DOTS;i++){
        j = float(i) - fract(iTime * TAU / 300.);
        theta = -j*PHI*TAU - length(uv);
        md = vec2(sin(theta),cos(theta))*sqrt(j/float(DOTS));
        dd *= pow(min(1.,length(uv-md)/arad),.25);

    }

    //dd /= sqrt(1. / float(DOTS+1));

    dd = pow(dd,2.5);

    //dd = (dd * (1.-dd) * 4.);
    //dd = (dd * (1.-dd) * 4.);

    //dd = abs(dd*2. - 1.);

    float posdd = pow(max(0.,dd*2.-1.),2.);
    float negdd = pow(-min(0.,dd*2.-1.),0.9);



    //vec3 col = vec3( posdd,0.,negdd );

    vec3 col = mix(mix(ZCOL,NCOL,negdd),PCOL,posdd);

    //float tval = cos(iTime*TAU / 20.)*3.+5.;



    // Output to screen
    fragColor = vec4(col,1.0);
}
