#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)

#define n1Q(p) p *= 2.*r(round(atan(p.x, p.y) * 4.) / 4.)
#define r(a) mat2(cos(a + asin(vec4(0,1,-1,0))))
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 n1,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(0.1,.02,3.),H(g*.1),.8)*1./e/8e3
    )
    {
        n1=g*d;
        n1.xz*=mat2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime));
        n1.yz*=mat2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime));
        n1.xy*=mat2(cos(iTime),sin(iTime),-sin(iTime),cos(iTime));
        n1Q(n1.xz);
             n1Q(n1.xy);
        n1.z-=iTime*10.5;
        a=30.;
        n1=mod(n1-a,a*2.)-a;
        s=2.;
        for(int i=0;i++<8;){
            n1=.3-abs(n1);
            n1.x<n1.z?n1=n1.zyx:n1;
            n1.z<n1.y?n1=n1.xzy:n1;
            s*=e=1.7+sin(iTime*.21)*.1;
            n1=abs(n1)*e-
                vec3(
                    5.*3.,
                    120,
                    8.*5.
                 );
         }
         g+=e=length(n1.xxzz)/s;
    }
}
