// Fork of "Interactive Fluid Simulation" by wyatt. https://shadertoy.com/view/XtGcDK
// 2024-10-05 12:04:22

// 2D vector field visualization by Morgan McGuire, @morgan3d, http://casual-effects.com
#define PI 3.1415927

// Choose your arrow head style
#define ARROW_TILE_SIZE 12.
#define ADAPTIVE_ARROW 0
// How sharp should the arrow head be? Used
const float ARROW_HEAD_ANGLE = 70.0 * PI / 180.0;

// Used for ARROW_LINE_STYLE
#define ARROW_HEAD_LENGTH (arrow_tile_size / 6.0)
const float ARROW_SHAFT_THICKNESS = .5;



// Computes the center pixel of the tile containing pixel pos
vec2 arrowTileCenterCoord(vec2 pos, float arrow_tile_size) {
	return (floor(pos / arrow_tile_size) + 0.5) * arrow_tile_size;
}


// v = field sampled at tileCenterCoord(p), scaled by the length
// desired in pixels for arrows
// Returns 1.0 where there is an arrow pixel.
float arrow(vec2 p, vec2 v, float arrow_tile_size) {
	// Make everything relative to the center, which may be fractional
	p -= arrowTileCenterCoord(p,arrow_tile_size);

    float mag_v = length(v), mag_p = length(p);

	if (mag_v > 0.0) {
		// Non-zero velocity case
		vec2 dir_p = p / mag_p, dir_v = v / mag_v;

		// We can't draw arrows larger than the tile radius, so clamp magnitude.
		// Enforce a minimum length to help see direction
		mag_v = clamp(mag_v, 5.0, arrow_tile_size / 2.0);

		// Arrow tip location
		v = dir_v * mag_v;

		// Define a 2D implicit surface so that the arrow is antialiased.
		// In each line, the left expression defines a shape and the right controls
		// how quickly it fades in or out.

        // Signed distance from a line segment based on https://www.shadertoy.com/view/ls2GWG by 
        // Matthias Reitinger, @mreitinger

        // Line arrow style
        float dist = 
            max(
                // Shaft
                ARROW_SHAFT_THICKNESS / 4.0 - 
                    max(abs(dot(p, vec2(dir_v.y, -dir_v.x))), // Width
                        abs(dot(p, dir_v)) - mag_v + ARROW_HEAD_LENGTH / 2.0), // Length

                 // Arrow head
                 min(0.0, dot(v - p, dir_v) - cos(ARROW_HEAD_ANGLE / 2.0) * length(v - p)) * 2.0 + // Front sides
                 min(0.0, dot(p, dir_v) + ARROW_HEAD_LENGTH - mag_v)); // Back

		return clamp(1.0 + dist, 0.0, 1.0);
	} else {
		// Center of the pixel is always on the arrow
		return max(0.0, 1.2 - mag_p);
	}
}

// by iq, https://iquilezles.org/articles/palettes/
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.283185*(c*t+d) );
}
void mainImage( out vec4 fragColor, in vec2 U )
{
    float gray = texture(iChannel0,U/iResolution.xy).a;
    gray = 1.1*log(1.+gray);
    // gamma
    gray = pow(gray,.9);
    float velocity = length(texture(iChannel0,U/iResolution.xy).xy);
    velocity = 4.*log(1.+velocity)-0.7;
    float pressure = texture(iChannel0,U/iResolution.xy).z;
    vec3 col = vec3(0.);
    vec3 a = vec3(0.650, 0.500, 0.310);
    vec3 b = vec3(-0.650, 0.500, 0.600);
    vec3 c = vec3(0.333, 0.278, 0.278);
    vec3 d = vec3(0.660, 0.000, 0.667);
    col = gray*palette(1.-gray, a, b, c, d);
    if (iMouse.x<iResolution.x*0.5 && iMouse.z>0. && MOUSE_OBSTACLE == 0 )
    {
      float LOGpressure = 0.2+2.*log(1.+pressure);
      col = palette(LOGpressure, a, b, c, d);
    }
    if (iMouse.x>iResolution.x*0.5 && iMouse.z>0. && MOUSE_OBSTACLE == 0 )
    {
      col = palette(1.-velocity, a, b, c, d);
    }

    if( SHOW_ARROW == 1 )
    {
        // draw arrow
        vec2 arrow_v = texture(iChannel0,U.xy/iResolution.xy).xy;
        //arrow_v.x-=0.05;
        float arrow_tile_size = ARROW_TILE_SIZE;
        if (ADAPTIVE_ARROW == 1)
        {
            arrow_tile_size*=gray;
            arrow_tile_size = clamp(arrow_tile_size,ARROW_TILE_SIZE*.8,ARROW_TILE_SIZE*1.2);
            col+=3.*log(gray+1.3)*arrow(U.xy, arrow_v * arrow_tile_size*gray, arrow_tile_size);
        }
        else
        {
            col+=2.*log(1.05+gray)*arrow(U.xy, arrow_v * arrow_tile_size, arrow_tile_size);
        }
    }

    // draw obstacle
    if(ENABLE_OBSTACLE == 1)
    {
        vec2 R = iResolution.xy;
        vec2 obstacleCenter = vec2(0.2 * R.x, 0.5 * R.y);
        if(MOUSE_OBSTACLE == 1)  obstacleCenter = iMouse.xy;
        if(ENABLE_OBSTACLE_MOVEMENT == 1) obstacleCenter = vec2(0.2 * R.x, 0.5 * R.y+80.*cos(iTime));
        float radius = OBSTACLE_RADIUS;
        if (length(U - obstacleCenter) < radius && length(U - obstacleCenter) > radius-2.) {
            col = vec3(1.);
        }
    }
    fragColor = vec4(col,0.);
}

vec2 R;
float ln (vec2 p, vec2 a, vec2 b) { 
    return length(p - a - (b - a) * clamp(dot(p - a, b - a) / dot(b - a, b - a), 0., 1.));
}

void mainImage(out vec4 Q, in vec2 U)
{   
    R = iResolution.xy;
    vec2 A = U + vec2(1, 0), B = U + vec2(0, 1), C = U + vec2(-1, 0), D = U + vec2(0, -1);

    vec4 u = texture(iChannel0, U / R);
    vec4 a = texture(iChannel0, A / R);
    vec4 b = texture(iChannel0, B / R);
    vec4 c = texture(iChannel0, C / R);
    vec4 d = texture(iChannel0, D / R);

    vec4 p = vec4(0.);
    vec2 g = vec2(0.);
    #define I 2
    for (int i = 0; i < I; i++) {
        U -= u.xy; A -= a.xy; B -= b.xy; C -= c.xy; D -= d.xy; 
        p += vec4(length(U - A), length(U - B), length(U - C), length(U - D)) - 1.;
        g += vec2(a.z - c.z, b.z - d.z);
        u = texture(iChannel0, U / R);
        a = texture(iChannel0, A / R);
        b = texture(iChannel0, B / R);
        c = texture(iChannel0, C / R);
        d = texture(iChannel0, D / R);
    }

    Q = u; 
    vec4 N = (a + b + c + d)/4.; 
    Q = mix(Q, N, vec4(0, 0, 1, 0)); 
    Q.xy -= g / 10. / float(I); 
    Q.z += (p.x + p.y + p.z + p.w) / 10.; 

    Q.z *= 0.99;

    if (length(U - vec2(0.05 * R.x, 0.5 * R.y)) < 10. ) 
    {
        Q.w = 1.;
        Q.xy = vec2(.7, 0.);
    }

    if (length(U - vec2(0.05 * R.x, 0.1 * R.y)) < 5. ) 
    {
        Q.w = 1.;
        Q.xy = vec2(.7, 0.);
    }

    if (length(U - vec2(0.05 * R.x, 0.9 * R.y)) < 5. ) 
    {
        Q.w = 1.;
        Q.xy = vec2(.7, 0.);
    }

    if (length(U - vec2(0.05 * R.x, 0.3 * R.y)) < 5. ) 
    {
        Q.w = 1.;
        Q.xy = vec2(.7, 0.);
    }

    // Add some ink and speed to the top and bottom of the screen
    if (length(U - vec2(0.05 * R.x, 0.7 * R.y)) < 5. ) 
    {
        Q.w = 1.;
        Q.xy = vec2(.7, 0.);
    }

    // Get mouse position and previous position from buffer B
    vec4 mouse = texture(iChannel1,vec2(0.5));
    // Find the distance of the line segment from the mouse start position to the end position
    float q = ln(U,mouse.xy,mouse.zw);
    vec2 m = mouse.xy-mouse.zw;
    float l = length(m);
    vec2 mouseVelocity = vec2(0.);
    if (mouse.z > 0. && l > 0.) {
    // Accelerating fluids and adding ink
    // Calculate the speed of the ink
    mouseVelocity = normalize(m) * min(l, 20.0) / 25.0;
    }
    if(ENABLE_OBSTACLE == 1)
    {
        vec2 obstacleCenter = vec2(0.2 * R.x, 0.5 * R.y);
        if(MOUSE_OBSTACLE == 1)  obstacleCenter = iMouse.xy;
        if(ENABLE_OBSTACLE_MOVEMENT == 1) obstacleCenter = vec2(0.2 * R.x, 0.5 * R.y+80.*cos(iTime));
        float radius = OBSTACLE_RADIUS;
        if (length(U - obstacleCenter) < radius+1.&& length(U - obstacleCenter) > radius-8.) {
                vec2 normal = normalize(U - obstacleCenter); 
                Q.xy = 1.1*reflect(Q.xy, normal) + 2.*mouseVelocity; 
                if (mouse.z > 0. && l > 0.) Q.xy = mix(Q.xy,mouseVelocity,.5);
                Q.w *= 1.01;
            }
        if (length(U - obstacleCenter) < radius) {
            vec2 normal = normalize(U - obstacleCenter); 
            Q.xyzw*=0.;
        }
    }


    Q.x += 0.0002;
    float noiseIntensity = 0.001;
    vec2 randomNoise = vec2(2.*texture(iChannel2,5.*U / iResolution.xy).r-1.);
    Q.xy += randomNoise * noiseIntensity;
    if (U.x < 1. || U.y < 1. || R.x - U.x < 1. || R.y - U.y < 1.) Q.xyzw*=0.;
}

