vec3 render(vec2 z) {
  z = z * 0.79 + vec2(-0.09, -0.87); // viewport
  const vec2 c = vec2(+0.26663020, +0.00467703);
  float l = 4.0; // add brightness
  for (int i = 0; i < 512; i++) {
    z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
    if (dot(z, z) > 1e4) break;
    l += 1.0;
  }
  l -= log2(log2(dot(z, z))); // smooth
  return 0.5 + 0.5 * cos(3.0 + l * 0.15 + vec3(0.0,0.6,1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  // uvx1
  vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.x;

  // anti-aliasing
  #define SIZE 2
  vec3 col = vec3(0.0);
  float sub = 1.0 / iResolution.x / float(SIZE);
  for (int i = 0; i < SIZE; i++) {
  for (int j = 0; j < SIZE; j++) {
    col += render(uv + vec2(i, j) * sub);
  }}
  col /= float((SIZE) * (SIZE));


  // output to screen
  fragColor = vec4(col, 1.0);
}
