void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    fragCoord.xy -= vec2(0.5);
    fragCoord.xy -= vec2(mod(fragCoord.x,iMouse.x),mod(fragCoord.y,iMouse.y));
    fragCoord.xy += vec2(0.5);

    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color


    // Output to screen
    fragColor = textureGrad(iChannel0,uv,vec2(iMouse.x/iResolution.x,0.),vec2(0.,iMouse.y/iResolution.y));
}
