// number of balls
#define N 15
#define A 1.
#define sqrtA 1.4142135

vec4 circle (vec2 st, vec2 pos, float radius,vec3 color){
    float pct = distance(st,pos);
    vec3 colorR = vec3(smoothstep(radius,0.,pct)) * color;
	return vec4(colorR, 1.0);
}



void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;
    st = st * 2.0 - 1.0;
    st.x *= iResolution.x/iResolution.y;
    fragColor = vec4(0.);

    for (int i = 0; i < N; i++) {
        float factor = float(i) / float(N) * 3.;
        // (x,y) - lemniscate of bernoulli
        float posX = (A * sqrtA * cos(iTime + factor)) / (1. + pow(sin(iTime + factor),2.));
        float posY = (A * sqrtA * cos(iTime + factor) * sin(iTime + factor)) / (1. + pow(sin(iTime + factor),2.)) * 1.5;
        float radius = abs(0.4 - factor);//+ pow(abs(sin((1.2 )*iTime)),3.);
        vec4 circleColor  = circle(vec2(st.x + posX, st.y + posY),vec2(0.),1. * radius,vec3(1. * radius,factor / 2.,0.3));

        fragColor = fragColor + circleColor;

    }
}
