void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-0.5;
    uv.x=uv.x*iResolution.x/iResolution.y;
    uv *= 1.-0.2*cos(2.0*iTime);
    uv.x+=0.7*sin(iTime);
    uv.y-=0.5*cos(iTime);
    float col = step(length(uv),0.25);
    fragColor=vec4(col*vec3(0.7,0.9,1.)+(1,-col)*vec3(0.,0.,0.),1.);
}
