// The final product of some live coding improv. The process is live narrated in this 
// video:
// On Youtube:  https://www.youtube.com/watch?v=0ifChJ0nJfM
// On Bilibili: https://www.bilibili.com/video/BV1dK411N7P7

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord / iResolution.xy;
	vec2 q = p - vec2(0.33,0.7);

	vec3 col = mix( vec3(1.0,0.3,0.5), vec3(1.0,1.8,0.3), sqrt(p.y) ); //modified vector patterns to resemble a bright yellowish pink

	float r = 0.2 + 0.1*cos( atan(q.y,q.x)*10.0 + 20.0*q.x + 1.0);
	col *= smoothstep( r, r+0.01, length( q / p ) ); //modified length(q) by dividing with p, making the palm tree smaller

	r = 0.015;
	r += 0.002*sin(120.0*q.y);
	r += exp(-40.0*p.y);
    col *= 1.0 - (1.0-smoothstep(r,r+0.002, abs(q.x-0.25*sin(2.0*q.y))))*(1.0-smoothstep(0.0,0.1,q.y));

	fragColor = vec4(col,1.0);
}
