float heart(vec2 uv)
{
    float top = sqrt((0.5*(-4.0 * uv.x*uv.x + 4.0)/1.5)*(-pow(0.5, abs(uv.x))+1.0));
    float bot = -1.3*sqrt((pow(0.23, abs(uv.x))-0.23));

    if ((top - uv.y)*(uv.y-bot) < 0.0)
        return 0.0;

    return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= vec2(0.5, 0.65);
    uv *= 2.0;

    fragColor = vec4(vec3(heart(uv)),1.0);
}
