//RENDERER

// FANCY COLORS
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

vec4 heatColor( in vec4 redFragColor ) {
    vec4 heatColor;

    // blue red palette
    vec3 a = vec3(0.650, 0.500, 0.310);
    vec3 b = vec3(-0.650, 0.500, 0.600);
    vec3 c = vec3(0.333, 0.278, 0.278);
    vec3 d = vec3(0.660, 0.000, 0.667);

    float redHeat = redFragColor.r;

    vec3 color = palette(redHeat, a, b, c, d);

    heatColor = vec4(color, 1.0);

    return heatColor;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy / iResolution.xy;

    // get frame data from buffer A

    vec4 frameData = texture(iChannel0, uv);

    vec4 redFragColor = vec4(frameData.rgb, 1.0);

    fragColor = heatColor(redFragColor);

    //See original color:
    //fragColor = vec4(frameData.rgb, 1.0);
}

// Laplacian Convolution
vec3 convolute(in mat3 filterKernel, in float divisionScalar , in vec2 uvCoord){

    // Offset for the neighboring pixels
    float Xoffset = 1.0 / iResolution.x;
    float Yoffset = 1.0 / iResolution.y;

    vec3 colorSum = vec3(0.0);

    // Loop over the kernel
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            vec2 offsetCoord = uvCoord + vec2(float(i) * Xoffset, float(j) * Yoffset);
            vec3 pixelColorAtCoord = texture(iChannel0, offsetCoord).rgb;
            float scalar = float(filterKernel[i+1][j+1]);

            vec3 addend = pixelColorAtCoord*scalar;
            colorSum += addend;

        }
    }

    // Average the sum
    vec3 color = colorSum / divisionScalar;

    return color;

}

// Chemical Reaction PDEs
vec3 chemicalReaction(in vec3 color, in vec2 uvCoord){

    // Find A and B Concentrations
    float A = color.r;
    float B = color.b;

    // find laplacian
    vec3 laplacian = convolute(laplacian, divisionFactor, uvCoord);
    float laplacianA = laplacian.r;
    float laplacianB = laplacian.b;

    float diegeticDeltaT = float( 1.0 / calculationsPerFrame );

    // find change
    float changeA = (ADiffusionFactor*laplacianA - A*B*B + feed*(1.0-A) )*diegeticDeltaT;
    float changeB = (BDiffusionFactor*laplacianB + A*B*B - (kill + feed)*B )*diegeticDeltaT;

    float newA = A + changeA;
    float newB = B + changeB;

    // Calculate new color
    color = vec3(newA, 0.0, newB);

    return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color;

    // get previousData
    vec4 previousFrameData = texture(iChannel0,uv);


    // Initial Condition
    if(iFrame == 0) {

        fragColor = initialize(uv, iResolution);
        return;
    }

    // Find color of next frame

    color = previousFrameData.rgb;

    for ( int i = 0; float(i) < calculationsPerFrame; i++ ){
        color = chemicalReaction(color, uv);
    }

    // Add Chemical B at mouse hold

    float mouseDistance = pow(iMouse.x - fragCoord.x, 2.0) + pow(iMouse.y - fragCoord.y, 2.0);

    if( mouseDistance < 14.0 && iMouse.z > 0.0) {
        color = vec3(0.0, 0.0, 1.0);
    }


    fragColor = vec4(color, 1.0);

}

