//Random
float rand(vec2 coordinate, float t){
    return fract(sin(t + dot(coordinate, vec2(12.9898, 78.233))) * 43758.5453);
}

// Initial Condition

vec4 initialize(in vec2 uvCoord, in vec3 resolution) {

    // aspect ratio
    uvCoord.x *= resolution.x / resolution.y;

    vec4 fragColor;

    //PRESETS

    //#define Annulus
    #define Noise

    #ifdef Annulus
        float radius = (uvCoord.y-0.5)*(uvCoord.y-0.5) + (uvCoord.x-0.88)*(uvCoord.x-0.88);
        bool condition = radius < 0.1 && radius > 0.05;
        if (condition) {
            fragColor = vec4(0.0, 0.0, 1.0, 1.0);

        } else {
            fragColor = vec4(1.0, 0.0, 0.0, 1.0);
        }
    #endif
    #ifdef Noise
        float concentrationA = rand(uvCoord, 0.4);

        bool condition = concentrationA < 0.05;

    #endif


    if (condition) {
        fragColor = vec4(0.0, 0.0, 1.0, 1.0);

    } else {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    }

    return fragColor;
}


