#define PI 3.1415926538
#define T iTime

vec3 hsv( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
	rgb = rgb*rgb*(3.0-2.0*rgb);
	return c.z * mix( vec3(1.0), rgb, c.y);}
    //smooth hsv from iq https://www.shadertoy.com/view/MsS3Wc

vec2 cMul(in vec2 z1, in vec2 z2) {
    return  mat2(z1,-z1.y,z1.x) * z2;
}

vec2 cDiv(in vec2 z1, in vec2 z2) {
    return z1 * mat2(z2,-z2.y,z2.x) / dot(z2,z2);
}

vec2 lattes(in vec2 z) {
    vec2 z_2 = cMul(z, z);
    vec2 numerator = z_2 + vec2(1.0, 0.);
    numerator = cMul(numerator, numerator);
    vec2 denom = cMul(vec2(4.0, cos(T*.03)), cMul(z, z_2 - vec2(1.0, cos(T*.07))));
    return cDiv(numerator, denom);
}

vec2 lattes_n(in vec2 z, in int n) {
    vec2 result = z;
    for (int i =0; i < n; i++) {
        result = lattes(result);
    }
    return result;
}

vec2 lattes_n_fl(in vec2 z, in float n) {
    vec2 result = z;
    int fl = int(floor(n));
    float factor = fract(n);
    vec2 low = lattes_n(z, fl);
    vec2 high = lattes(low);
    return (1. - factor) * low + factor * high;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = 4. * (2.*fragCoord-iResolution.xy) / max(iResolution.x, iResolution.y);   
    float factor = abs(sin(T * .1)) * 5.;
    p = lattes_n_fl(p, factor); // 2 fixed points, loxodromic    
    float len = length(p);           
    //Thank you to Fabrice for the anti anliasing tip
    fragColor = vec4( hsv(vec3(log(len)*.1,1.,clamp(len*.5,.0,.8))),0.);
}
