void mainImage(out vec4 fg, in vec2 fc) {
    vec2 ir = iResolution.xy, uv = fc.xy / ir * vec2((ir.x / ir.y), 1.) - vec2(1.,.5);
    float r=1./length(uv), t=iTime, p=mod(atan(uv.y,uv.x)/3.14+r+sin(t),1.);
    fg = (mod(r + t, 2.) < 1. && mod(p, .5) < .25) ? vec4(.9/r) : vec4(0);
}
