void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int maxI = 512;
    int n = int(iTime * 5.);
    ivec2 uv = ivec2(100. * (2.*fragCoord - iResolution.xy)/iResolution.y);
    for(int i; i < maxI; i++)
    {
        if(n % uv.x == 0)
        {
            n /= uv.x;
        }
        else
        {
            n = n*uv.y + 1;
        }
    }
    fragColor = vec4(n - 4);
}
