const float pi = acos(-1.0);
const float pi2 = pi*2.;
vec3 pale_aqua=vec3(0.76,0.89,0.89);
vec3 wedgwood_blue=vec3(0.08,0.51,0.67);
vec3 turquoise_blue=vec3(0.0,0.68,0.8);

mat2 rotate2d(float angle)
{
	return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}
vec2 PolarMod(vec2 p, float r)
{
    float a =  atan(p.x, p.y) + pi/r;
    float n = pi2 / r;
    a = floor(a/n)*n;
    return p*rotate2d(-a);
}
float hash12(vec2 p) 
{
	vec3 p3 = fract(vec3(p.xyx) * .1031);
	p3 += dot(p3, p3.yzx + 33.33);
	return fract((p3.x + p3.y) * p3.z);
}
float ValueNoise21(vec2 p) 
{
    vec2 i = floor(p);
    float v00 = hash12(i);
    float v01 = hash12(i + vec2(0.0, 1.0));
    float v10 = hash12(i + vec2(1.0, 0.0));
    float v11 = hash12(i + vec2(1.0, 1.0));
    vec2 f = fract(p);
    f = f * f * (3.0 -2.0 * f); 
    return mix(mix(v00, v10, f.x), mix(v01, v11, f.x), f.y)-0.5;
}
float FBM24_2(vec2 uv, float amplitude, float frequency)
{
	float result = 0.;
	float amplitude2 = amplitude;
	float frequency2 = frequency;
	for (int i = 0; i < 4; i++)
	{
        result += ValueNoise21(uv * frequency2) * amplitude2;
		amplitude2 *= 0.5;
		frequency2 *= 2.0;
	}
	return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    float col =FBM24_2(PolarMod(p*4.0,8.0),3.0,2.0);
    vec3 tcol;

    if(col<0.2)
    {
    }
    else if(col<0.5)
    {
        tcol = pale_aqua;
    }
    else if(col<0.7)
    {
        tcol = turquoise_blue;
    }

    fragColor = vec4(tcol, 1.0);
}
