void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   //vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
  vec2 pos = fragCoord.xy /iResolution.xy;
  vec3[4] col4 = vec3[](
    vec3(1.0, 0.3, 0.1),
    vec3(0.1, 0.3, 1.0),
    vec3(0.1, 1.0, 0.3),
    vec3(0.7, 1.0, 0.3)
  );
  vec3 col = mix(mix(col4[0], col4[1], pos.x), mix(col4[2], col4[3], pos.x), pos.y);

  pos = (fragCoord.xy*2.0 - iResolution.xy)/iResolution.y;
  vec2 mousePos = (iMouse.xy*2.0-iResolution.xy)/ iResolution.y;
  bool circle = length(pos-mousePos) < 0.3;

  if(circle) col=vec3(circle);
  fragColor = vec4(col,1.0);   
}
