vec3 palette( in float t)
{
    vec3 a = vec3(0.676, 0.243, 0.712);
    vec3 b = vec3(0.326, 0.738, 0.690);
    vec3 c = vec3(0.836, 1.197, 0.910);
    vec3 d = vec3(3.640, 2.556, 1.996);
    return a + b*cos( 6.283185*(c*t+d));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy; // normalize xy coords to range in [0,1]
    uv = (uv - 0.5)*2.0; // make the center (0,0). xy range is now [-1,1]
    uv.x *= iResolution.x/iResolution.y;
    vec2 guv = uv;

    vec3 finalColor = vec3(0.0);

    for(float i =1.0; i<3.0; i++)
    {
    uv *=1.5;
    uv = fract(uv);
    uv -=0.5;
    float d = length(uv) * exp(-length(guv));

    vec3 col = palette(length(guv) + sqrt(iTime));

    d = abs(sin(10.*d + iTime)/10.);
    d = 0.02/d;
    d = pow(d, 1.5);

    finalColor += 0.1/-col*d;
    }

    fragColor = vec4(finalColor, 1.0);

}
