float plot(vec2 pnt)
{
    float thickness = 6.0;
    float a = (sin(2.0*iTime) * 0.5 + 0.5) * thickness;
    return smoothstep(a, 0.0, abs(pnt.y - pnt.x));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 st = fragCoord / iResolution.xy;

    float p = plot(st);

    fragColor = vec4(vec3(p), 1.0);
}
