void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float angle = iTime * 0.05;
    vec2 normCord = fragCoord / iResolution.xy * 2.0 - 1.0;
    for (float i = 0.0; i < 128.0; i += 1.0)
    {
        normCord = abs(normCord);
        normCord -= 0.5;
        normCord *= 1.05;
        normCord *= mat2 (
              cos(angle), -sin(angle),
              sin(angle), cos(angle)
        );
    }
    fragColor = vec4(length(normCord) + 0.00,
        length(normCord) + 0.08,
        length(normCord) + 0.1,
        1.0);
}
