void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Draw circle at 180px with radius 50.0
    float circle = length(fragCoord - vec2(180.0, 80.0)) - 50.0;

    // Split screen colors from 180.0 px
    vec4 bg = mix(vec4(0.5,  0.5, 0.5, 1.0), vec4(0.2, 0.2, 0.2, 1.0), step(180.0, fragCoord.x));

    bg += min(bg, vec4(circle));

    fragColor = bg;
}
