const vec2 size = vec2(800, 450);
const vec2 center = vec2(0.5, 0.5);
const float radius = 100.0;
const bool reverse = false;

vec4 position_get_color(vec2 position)
{
    return texture(iChannel0, position / size);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 center_point = size * center;
    vec2 vector = fragCoord.xy - center_point;
    float vector_length = length(vector);
    float radius_add = radius * 0.28 * float(int(reverse) * 2 - 1);

    vector_length *= smoothstep(0.0, 1.0, (vector_length - radius) / radius_add);

    fragColor = position_get_color(center_point + normalize(vector) * vector_length);
}
