void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv *= 10.0;
    float r = sin(uv.x + sin(iTime)) * 0.5 + 0.5;
    float g = sin(uv.y + cos(iTime)) * 0.5 + 0.5;
    float b = sin(uv.x + uv.y + sin(iTime)) * 0.5 + 0.5;
    r *= sin(iTime * 2.0 + uv.y) * 0.5 + 0.5;
    g *= cos(iTime * 2.0 + uv.x) * 0.5 + 0.5;
    b *= sin(iTime * 2.0 + uv.x + uv.y) * 0.5 + 0.5;
    fragColor = vec4(r, g, b, 1.0);
}
