float dts(vec2 xy, float sh, float scy, float scx) {
    return abs(sin(xy.x*scx+sh)/scy-xy.y);
}

float rnd(float x, float n) {
    return fract(1e4*sin(x*10.0+10.0*pow(1.0+x, 2.5)+n*(2.0+x)*(2.0+x)));
}

#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x/iResolution.x, fragCoord.y / iResolution.y - 0.5);
    const float n = 21.0;
    float cf = 0.0;
    for(float j=0.0;j<n;++j) {
        float d= dts(uv, 2.0*PI*rnd(j,2.0)-(1.0+rnd(j,1.0)*2.0)*iTime/4.0, 3.0+rnd(j, 0.0), 2.0+rnd(j,4.0)*2.0)*uv.x;
        d = pow(d,1.5)*1e4;
        d = 2.0/(d+1.0);
        cf += d/8.0*pow(1.0-uv.x,0.8);
    }
    vec3 col=vec3(clamp(cf*3.0,0.0,1.0),clamp(cf*3.0-1.0,0.0,1.0), clamp(cf*3.0-2.0,0.0,1.0));

    fragColor = vec4(col,1.0);
}
